package CoreJavaConcepts;

public class Series_b {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		for (int i=1;i<=25;i++)
			
		{
			String s=Integer.toString(i);
			System.out.println("1/"+s+"="+((float)1/i) +", ");
		}

		System.out.println("continued....");
	}

}
